import React from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {NavLink, useLocation} from "react-router-dom";
import {LOGIN_ROUTE, REGISTRATION_ROUTE} from "../utils/consts";

const Auth = () => {
    const location = useLocation()
    const isLogin = location.pathname === LOGIN_ROUTE
    console.log(location)
    return (
        <Container
            className="d-flex justify-content-center align-items-center "
            style={{height: window.innerHeight-54}}
        >
            <Card style={{width:500}} className="p-2">
                <h2 className="m-auto">{isLogin? 'Вход' : 'Регистрация'}</h2>
                <Form className="d-flex flex-column">
                    <Form.Control
                        className="mt-3"
                        placeholder="Почта"
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Пароль"
                    />
                    {isLogin ? "" :
                        <Form.Control
                            className="mt-3"
                            placeholder="Повторите пароль"
                        />
                    }
                    {/*<Button className="mt-3">{isLogin? 'Войти': 'Зарегистрироваться'}</Button>*/}
                    {isLogin ?
                        <div className="d-flex justify-content-center mt-2">Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}> Зарегистрироваться</NavLink></div>
                        :
                        <div className="d-flex justify-content-center mt-2">Уже есть аккаунт? <NavLink to={LOGIN_ROUTE}> Войти</NavLink></div>
                    }
                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
