import React, {useContext} from 'react';
import {Context} from "../index";
import {Button, Container, Nav, Navbar, NavDropdown, NavLink} from "react-bootstrap";
import { BrowserRouter, Route, Link} from "react-router-dom";
import {observer} from "mobx-react-lite";
import styles from '../styles.css';
import {MAIN_ROUTE, MODERATION_ROUTE, STATISTICS_ROUTE} from "../utils/consts";

const NavBar = observer(() => {
    const {user} = useContext(Context)
    return (

        <Navbar expand="lg" className="bg_color pt-3">
            <Container >
                <Navbar.Brand href="#home" className="logo" >a_toxic</Navbar.Brand>
                <Navbar.Collapse id="basic-navbar-nav">
                    {
                        user.isAuth?
                            <Nav className="ml-auto">
                                <Nav.Link as={Link} to={MAIN_ROUTE} >Главная</Nav.Link>
                                <Nav.Link as={Link} to={MODERATION_ROUTE}>Модерация контента</Nav.Link>
                                <Nav.Link as={Link} to={STATISTICS_ROUTE}>Статистика</Nav.Link>
                                <Nav.Link href="#link3">Выйти</Nav.Link>
                            </Nav>
                            :
                            <Nav className="ml-auto">
                                <Nav.Link href="#link2" onClick={()=>user.setIsAuth(true)}>Войти</Nav.Link>
                            </Nav>
                    }

                </Navbar.Collapse>
            </Container>

        </Navbar>
    );
});

export default NavBar;
